import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const TreeContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  position: relative;
  height: 600px;
  width: 800px;
  margin: auto;
`;

const Branch = styled(motion.div)`
  position: absolute;
  background-color: ${({ color }) => color || '#ddd'};
  border-radius: 50%;
  padding: 1em;
  width: 150px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  color: white;
  font-weight: bold;
  cursor: pointer;
`;

const branches = [
  { text: 'Cognitive', top: '10%', left: '10%', color: '#4CAF50' },
  { text: 'Leadership', top: '10%', left: '70%', color: '#E91E63' },
  { text: 'Collaboration', top: '50%', left: '80%', color: '#00BCD4' },
  { text: 'Creativity', top: '50%', left: '10%', color: '#FFC107' },
  { text: 'Emotional', top: '75%', left: '30%', color: '#FF9800' },
  { text: 'Social & Civic Engagement', top: '75%', left: '60%', color: '#3F51B5' },
];

const subSkills = {
  Cognitive: ['Problem Solving', 'Critical Thinking', 'System Thinking'],
  Leadership: ['Entrepreneurship', 'Organizing', 'Influence'],
  Collaboration: ['Negotiation', 'Conflict Mgmt.', 'Communication'],
  Creativity: ['Curiosity', 'Innovation', 'Visualization'],
  Emotional: ['Empathy', 'Resilience', 'Self-awareness'],
  'Social & Civic Engagement': ['Citizenship', 'Sustainability', 'Equality'],
};

const TreeBranch = ({ text, color, top, left }) => {
  return (
    <Branch
      style={{ top, left }}
      color={color}
      whileHover={{ scale: 1.1 }}
      transition={{ duration: 0.3 }}
    >
      {text}
    </Branch>
  );
};

export const SkillsTree = () => {
  return (
    <TreeContainer>
      <h2>Tree of Skills</h2>
      {branches.map((branch, index) => (
        <TreeBranch
          key={index}
          text={branch.text}
          color={branch.color}
          top={branch.top}
          left={branch.left}
        />
      ))}
    </TreeContainer>
  );
};
