import React from "react";
import Home from "./components/Home"; // Home component
import About from "./components/About"; // About component
import Skills from "./components/Skills"; // Skills component (with SkillsGrid)
import Projects from "./components/Projects"; // Projects component
import Contact from "./components/Contact"; // Contact component
import styled from "styled-components";
import { GlobalStyles } from "./styles"; // Global styles (if you have them)
import { motion } from "framer-motion"; // Framer Motion for animations

// Styled container for the whole app layout
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  font-family: 'Arial', sans-serif;
  background-color: #f0f0f0;
`;

function App() {
  return (
    <>
      <GlobalStyles /> {/* Add global styles if any */}
      <AppContainer>
        {/* Home Section with Animation */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1.5 }}
        >
          <Home />
        </motion.div>

        {/* About Section with Animation */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.5 }}
        >
          <About />
        </motion.div>

        {/* Skills Section with Animation (SkillsGrid Integrated) */}
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1.5 }}
        >
          <Skills /> {/* SkillsGrid with animated skills cards */}
        </motion.div>

        {/* Projects Section with Animation */}
        <motion.div
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.5 }}
        >
          <Projects />
        </motion.div>

        {/* Contact Section with Animation */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1.5 }}
        >
          <Contact />
        </motion.div>
      </AppContainer>
    </>
  );
}

export default App;
