import React from 'react';
// import styled from 'styled-components';
import styled, { keyframes } from 'styled-components';
// Container for the contact section
const ContactSection = styled.section`
  background: linear-gradient(135deg, #333 0%, #F5F5DC 100%);
  padding: 4em 2em;
  text-align: center;
`;

// Title for the section
const Title = styled.h1`
  font-size: 2.5em;
  color: #333;
  margin-bottom: 1em;
`;


// Keyframe for border animation
const borderAnimation = keyframes`
  0% {
    border-color: rgba(132, 176, 103, 0);
  }
  100% {
    border-color: rgba(132, 176, 103, 1);
  }
`;
// Fancy and animated contact button
const ContactButton = styled.a`
  background: linear-gradient(135deg, #84b067 0%, #2c3e50 100%);
  color: white;
  padding: 1em 2em;
  text-decoration: none;
  border-radius: 50px;
  margin-top: 1em;
  display: inline-block;
  font-size: 1.2em;
  text-transform: uppercase;
  border: 2px solid rgba(132, 176, 103, 0); /* Start with transparent border */
  transition: background-position 0.6s ease, transform 0.3s ease, border-color 0.4s ease;
  background-size: 200% 200%;
  box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.2);

  &:hover {
    background-position: right center; /* Changes background position for smooth color change */
    transform: translateY(-5px); /* Lifts the button slightly on hover */
    box-shadow: 0px 15px 25px rgba(0, 0, 0, 0.4); /* Deepens shadow on hover */
    animation: ${borderAnimation} 0.4s forwards; /* Trigger border animation on hover */
  }

  &:active {
    transform: translateY(2px); /* Press effect on click */
    box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.2);
  }
`;

// Contact info styled component
const ContactInfo = styled.div`
  margin: 2em 0;
`;

const InfoItem = styled.p`
  font-size: 1.2em;
  margin: 0.5em 0;
`;

const Link = styled.a`
  color: #333;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const Contact = () => {
  return (
    <ContactSection>
      <Title>Contact Me</Title>
      <ContactButton href="mailto:tanishqjain010@gmail.com">Send me an Email</ContactButton>
      
      <ContactInfo>
        {/* LinkedIn */}
        <InfoItem>
          LinkedIn: <Link href="https://www.linkedin.com/in/tanishqjain010/" target="_blank">Tanishq Jain</Link>
        </InfoItem>

        {/* Mobile Number */}
        <InfoItem>Mobile: +91-9479811499</InfoItem>

        {/* Slack Username */}
        <InfoItem>Slack: nanu1605</InfoItem>

        {/* GitHub */}
        <InfoItem>
          GitHub: <Link href="https://github.com/nanu1605" target="_blank">nanu1605</Link>
        </InfoItem>
      </ContactInfo>
    </ContactSection>
  );
};

export default Contact;
