import styled from 'styled-components';
import { motion } from 'framer-motion';



export const ExperienceSection = styled.section`
  position: relative;
  background: linear-gradient(135deg, #F5F5DC 0%, #4ca1af 100%);
  padding: 4em 2em;
  text-align: center;
  overflow: hidden;
`;

export const Title = styled.h1`
  font-size: 3em;
  font-family: 'Pacifico', cursive;
  color: #333;
  margin-bottom: 2em;
  text-transform: uppercase;
  letter-spacing: 3px;
`;

export const ExperienceList = styled(motion.div)`
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 3em;
  perspective: 1000px;
`;

export const ExperienceItem = styled(motion.div)`
  background: linear-gradient(135deg, #333 0%, #84b067 100%);
  font-family: 'Pacifico', cursive;
  border-radius: 15px;
  padding: 2em;
  box-shadow: 0px 15px 25px rgba(0, 0, 0, 0.2);
  transition: transform 0.6s ease, box-shadow 0.6s ease;
  position: relative;
  background-size: 200% 200%;
  transform: rotate3d(1, 1, 1, 10deg);
  cursor: pointer;
  overflow: hidden;

  &:hover {
    transform: scale(1.2) rotate3d(1, 1, 1, 360deg);
    box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
  }
`;

export const JobTitle = styled.h3`
  font-size: 2.2em;
  color: #fff;
  font-weight: bold;
  margin-bottom: 0.5em;
`;

export const Company = styled.h4`
  font-size: 1.5em;
  color: #fff;
  font-weight: bold;
  margin-bottom: 1em;
`;

export const Timeline = styled.h5`
  font-size: 1.1em;
  color: #ccc;
  margin-bottom: 1em;
`;

export const Description = styled.p`
  font-size: 1.2em;
  color: #fff;
  line-height: 1.8;
`;
