import React from 'react';
import styled from 'styled-components';

// Styled container for the contact section
const ContactSection = styled.section`
  padding: 4em;
  background: #333;
  color: white;
  text-align: center;
  min-height: 100vh;
`;

// Styled button with animation
const EmailButton = styled.a`
  display: inline-block;
  padding: 1em 2em;
  background: linear-gradient(135deg, #84b067 0%, #2c3e50 100%);
  color: white;
  font-size: 1.2em;
  border: none;
  border-radius: 50px;
  text-transform: uppercase;
  text-decoration: none;
  transition: background 0.4s, transform 0.3s ease;
  box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.2);

  &:hover {
    background: linear-gradient(135deg, #2c3e50 0%, #84b067 100%);
    transform: translateY(-5px); /* Lifts the button slightly on hover */
    box-shadow: 0px 15px 25px rgba(0, 0, 0, 0.4); /* Deepens shadow on hover */
  }

  &:active {
    transform: translateY(2px); /* Press effect on click */
    box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.2);
  }
`;

const Contact = () => {
  return (
    <ContactSection>
      <h2>Contact Me</h2>
      <p>You can reach me via email by clicking the button below:</p>
      <EmailButton href="mailto:your-email@example.com">
        Send me an Email
      </EmailButton>
    </ContactSection>
  );
};

export default Contact;
