import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Section container for About Me
const AboutSection = styled.section`
  position: relative;
  background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  padding: 4em 2em;
  text-align: center;
  overflow: hidden;
  color: white;
`;

// Container for content
const ContentWrapper = styled.div`
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 2em;
  text-align: left;
`;

// Animated text for section title
const Title = styled(motion.h1)`
  font-size: 3em;
  color: #fff;
  margin-bottom: 1em;
  text-transform: uppercase;
  letter-spacing: 3px;
  background: -webkit-linear-gradient(135deg, #ff9a9e, #fad0c4);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

// Animated image for profile
const ProfileImage = styled(motion.img)`
  max-width: 200px;
  border-radius: 50%;
  margin: 0 auto;
  box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.1);
`;

// Fancy paragraph with hover effect
const FancyText = styled(motion.p)`
  font-size: 1.2em;
  line-height: 1.8;
  color: #fff;
  transition: color 0.3s ease;
  text-align: justify;
  
  &:hover {
    color: #ffd700; /* Change text color on hover */
  }
`;

// Container for profile details
const ProfileDetails = styled(motion.div)`
  margin-top: 2em;
  text-align: left;
`;

// Animated border on hover
const BorderBox = styled(motion.div)`
  border: 2px solid transparent;
  padding: 20px;
  border-radius: 15px;
  background: linear-gradient(#fff, #fff) padding-box,
    linear-gradient(135deg, #ff9a9e 0%, #fad0c4 100%) border-box;
  margin: 0 auto;
  transition: transform 0.4s ease;
  
  &:hover {
    transform: scale(1.05);
  }
`;

const AboutMe = () => {
  return (
    <AboutSection>
      <ContentWrapper>
        {/* Title with text reveal on scroll */}
        <Title
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
        >
          About Me
        </Title>

        {/* Profile Image with fade-in and scale effect */}
        <ProfileImage
          src="path_to_your_profile_image.jpg"
          alt="Profile Image"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
        />

        {/* Fancy text with hover effect */}
        <FancyText
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.8 }}
        >
          Hi, I'm [Your Name], a DevOps Engineer with experience in CI/CD, Kubernetes, Docker, and automation. I am passionate about building scalable infrastructures and streamlining deployment processes. My goal is to bring efficiency, innovation, and high standards to every project I work on.
        </FancyText>

        {/* Profile details section with hover border effect */}
        <BorderBox>
          <ProfileDetails
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4, duration: 0.8 }}
          >
            <p><strong>Current Role:</strong> Associate Software Engineer, BOSCH Global Software Technologies</p>
            <p><strong>Skills:</strong> Python, Docker, Kubernetes, Jenkins, Terraform, AWS, Azure</p>
            <p><strong>Contact:</strong> [Your Email] | [Your LinkedIn]</p>
          </ProfileDetails>
        </BorderBox>
      </ContentWrapper>
    </AboutSection>
  );
};

export default AboutMe;
