import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Me from '../assets/Me.jpg';

// Home section styling
const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4em 2em;
  background: linear-gradient(135deg, #333, #4ca1af);
  color: white;
  text-align: center;
  min-height: 100vh;
`;

// Title and subtitle
const HeroTitle = styled.h1`
  font-size: 4em;
  margin-bottom: 0.5em;
`;

const HeroSubtitle = styled.p`
  font-size: 1.5em;
  white-space: nowrap;
  overflow: hidden;
  border-right: 2px solid rgba(255, 255, 255, 0.75);
  padding-right: 5px;
  display: inline-block;
`;

// Floating photo with animation
const AnimatedPhoto = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  border: 5px solid white;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.5);
`;

// Typewriter effect using React hooks
const Home = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = 'DevOps Engineer | Full-Stack Developer';

  useEffect(() => {
    let index = 0;
    const typingSpeed = 100;

    const typeEffect = () => {
      if (index < fullText.length) {
        setDisplayedText((prev) => prev + fullText[index]);
        index++;
      } else {
        clearInterval(typingInterval);
      }
    };

    const typingInterval = setInterval(typeEffect, typingSpeed);

    return () => clearInterval(typingInterval); // Cleanup the interval on unmount
  }, []);

  return (
    <HomeSection>
      <AnimatedPhoto src={Me} alt="Tanishq Jain" />
      <HeroTitle>Hi, I'm Tanishq Jain</HeroTitle>
      <HeroSubtitle>{displayedText}</HeroSubtitle>
    </HomeSection>
  );
};

export default Home;
