// import React from "react";
// import Home from './components/Home';
// import About from "./components/About";
// import Experience from "./components/Experience";
// import Skills from "./components/Skills";
// import Projects from "./components/Projects";
// import Contact from "./components/Contact";
import { GlobalStyles } from "./styles";
import { motion } from "framer-motion";

import React, { useContext } from 'react';
import { ThemeProvider, ThemeContext } from './ThemeContext'; // Import ThemeProvider and ThemeContext
import './App.css'; // Main CSS file

// Import other components (e.g., Home, About, Skills, Projects, etc.)
import Home from './components/Home';
import About from './components/About';
import Skills from './components/Skills';
import Projects from './components/Projects';
import Contact from './components/Contact';

function App() {
  const { theme, toggleTheme } = useContext(ThemeContext); // Use theme context

  // Apply the theme class to the body element
  document.body.className = theme;

  return (
    <ThemeProvider>
      <div className={`App ${theme}`}>
        <header className={`App-header ${theme}`}>
          <button className={`toggle-button ${theme}`} onClick={toggleTheme}>
            Toggle to {theme === 'light' ? 'Dark' : 'Light'} Mode
          </button>
        </header>
      <GlobalStyles />
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <Home />
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.5 }}
      >
        <About />
      </motion.div>

      {/* Add Experience component here */}
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.5 }}
      >
        <Experience />
      </motion.div>

      <motion.div
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 1.5 }}
      >
        <Skills />
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.5 }}
      >
        <Projects />
      </motion.div>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <Contact />
      </motion.div>
      </div>
    </ThemeProvider>
  );
}

export default App;
