import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Container for the skill card
const Card = styled.div`
  perspective: 1000px; /* Define the 3D space for the card to flip in */
  width: 200px;
  height: 300px;
  margin: 20px;
  position: relative;
`;

// Card inner container that will rotate
const CardInner = styled(motion.div)`
  position: absolute;
  width: 100%;
  height: 100%;
  transition: transform 0.8s;
  transform-style: preserve-3d; /* Allow the 3D flip effect */
  
  ${Card}:hover & {
    transform: rotateY(180deg); /* Flip the card when hovering */
  }
`;

// Front face of the card
const CardFront = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden; /* Hide the back when front is visible */
  background: #84b067;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5em;
  border-radius: 15px;
`;

// Back face of the card
const CardBack = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden; /* Hide the front when back is visible */
  background: #333;
  color: white;
  transform: rotateY(180deg); /* Flip the back to align correctly */
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2em;
  border-radius: 15px;
`;

// The SkillCard component with flip animation
const SkillCard = ({ skill, details }) => {
  return (
    <Card>
      <CardInner>
        <CardFront>
          {skill} {/* Front side displaying the skill name */}
        </CardFront>
        <CardBack>
          {details} {/* Back side displaying the details */}
        </CardBack>
      </CardInner>
    </Card>
  );
};

export default SkillCard;
