import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion'; // For advanced animations

// Section container for experience
const ExperienceSection = styled.section`
  background-color: #f7f7f7;
  padding: 4em 2em;
  text-align: center;
`;

// Title for the section
const Title = styled.h1`
  font-size: 2.5em;
  color: #333;
  margin-bottom: 1em;
`;

// Container for experience items
const ExperienceList = styled(motion.div)`
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 2.5em;
`;

// Individual experience card with hover and scale effect
const ExperienceItem = styled(motion.div)`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 15px;
  padding: 2em;
  box-shadow: 0px 15px 25px rgba(0, 0, 0, 0.1);
  transition: transform 0.4s ease, box-shadow 0.4s ease, background 0.4s ease;
  position: relative;
  cursor: pointer;
  
  &:hover {
    transform: scale(1.1) rotateY(5deg); /* Scaling and rotation effect on hover */
    background: linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%); /* Color transition on hover */
    box-shadow: 0px 25px 40px rgba(0, 0, 0, 0.2);
  }
`;

const JobTitle = styled.h3`
  font-size: 1.8em;
  color: #333;
  font-weight: bold;
  margin-bottom: 0.5em;
  transition: color 0.3s ease;

  ${ExperienceItem}:hover & {
    color: #ffffff; /* Change text color to white on hover */
  }
`;

const Company = styled.h4`
  font-size: 1.5em;
  color: #333;
  font-weight: bold;
  transition: color 0.3s ease;

  ${ExperienceItem}:hover & {
    color: #ffffff; /* Change text color to white on hover */
  }
`;

const Timeline = styled.h5`
  font-size: 1.1em;
  color: #888;
  margin-bottom: 1em;
`;

const RoleTitle = styled.h6`
  font-size: 1.2em;
  color: #555;
  font-weight: bold;
  margin-top: 1.5em;
  margin-bottom: 0.5em;
  background-color: #f0f0f0;
  padding: 0.5em;
  border-radius: 10px;
`;

const Description = styled.p`
  font-size: 1em;
  color: #555;
  line-height: 1.6;
  transition: color 0.3s ease;

  ${ExperienceItem}:hover & {
    color: #ffffff; /* Change text color to white on hover */
  }
`;

const BulletPoint = styled.li`
  font-size: 1em;
  margin: 0.5em 0;
  color: #666;
  transition: color 0.3s ease;

  ${ExperienceItem}:hover & {
    color: #ffffff; /* Change bullet point color to white on hover */
  }
`;

// Framer Motion animation variants
const experienceVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.8, ease: 'easeInOut', staggerChildren: 0.3, delayChildren: 0.2 },
  },
};

// Framer Motion animation variants for individual cards
const cardVariants = {
  hidden: { opacity: 0, y: 50, scale: 0.9 },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    rotateY: 0,  // Initial state of no rotation
    transition: { duration: 0.5, ease: 'easeInOut', delay: 0.1 },
  },
};

const Experience = () => {
  return (
    <ExperienceSection>
      <Title>My Experience</Title>
      <ExperienceList
        initial="hidden"
        animate="visible"
        variants={experienceVariants} // Staggered animation for each experience
      >
        {/* BOSCH Experience */}
        <ExperienceItem variants={cardVariants}>
          <JobTitle>Associate Software Engineer (DevOps & Full Stack)</JobTitle>
          <Company>BOSCH Global Software Technologies, Bangalore, India</Company>
          <Timeline>Jul. 2023 - Present</Timeline>

          {/* DevOps Engineer Role */}
          <RoleTitle>DevOps Engineer Role</RoleTitle>
          <Description>
            <ul>
              <BulletPoint>Architected and maintained CI/CD pipelines.</BulletPoint>
              <BulletPoint>Engineered a custom Jenkins Plugin, reducing build times by 40%.</BulletPoint>
              <BulletPoint>Led Docker containerization, boosting application scalability by 30%.</BulletPoint>
              <BulletPoint>Implemented auto-scaling in Kubernetes, reducing manual intervention by 60%.</BulletPoint>
            </ul>
          </Description>

          {/* Full Stack Engineer Role */}
          <RoleTitle>Full Stack Engineer Role</RoleTitle>
          <Description>
            <ul>
              <BulletPoint>Developed a “docs-as-code” framework reducing manual effort by 40 hours monthly.</BulletPoint>
              <BulletPoint>Created an automated visualization tool integrating Sphinx with Graphviz and PlantUML.</BulletPoint>
              <BulletPoint>Deployed RBAC strategy on cloud infrastructure with zero data breaches.</BulletPoint>
            </ul>
          </Description>
        </ExperienceItem>

        {/* Google Summer of Code Experience */}
        <ExperienceItem variants={cardVariants}>
          <JobTitle>Google Summer of Code Contributor</JobTitle>
          <Company>Google Summer of Code 2022 - PEcAn Projects</Company>
          <Timeline>Jun. 2022 - Sept 2022</Timeline>
          <Description>
            <ul>
              <BulletPoint>Refactored 10+ R packages for CRAN compliance.</BulletPoint>
              <BulletPoint>Engineered critical R functions to eliminate non-CRAN dependencies.</BulletPoint>
              <BulletPoint>Optimized download process for PEcAn packages.</BulletPoint>
            </ul>
          </Description>
        </ExperienceItem>
      </ExperienceList>
    </ExperienceSection>
  );
};

export default Experience;
