import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const TreeContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  position: relative;
  height: 600px;
  width: 800px;
  margin: auto;
`;

const Branch = styled(motion.div)`
  position: absolute;
  background-color: ${({ color }) => color || '#ddd'};
  border-radius: 50%;
  padding: 1em;
  width: 150px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  color: white;
  font-weight: bold;
  cursor: pointer;
  z-index: 1;
`;

const SubSkill = styled(motion.div)`
  position: absolute;
  background-color: #f9f9f9;
  padding: 0.5em;
  border: 1px solid #ddd;
  border-radius: 5px;
  z-index: 2;
  transition: opacity 0.3s ease-in-out;
  opacity: ${({ visible }) => (visible ? 1 : 0)};
`;

// Updated positions for sub-skills in an arc shape below the main skill
const subSkillArcPositions = {
  Languages: [
    { top: '100px', left: '-100px' }, // Arc Left
    { top: '130px', left: '0px' },    // Arc Center
    { top: '100px', left: '100px' },  // Arc Right
    { top: '160px', left: '-150px' }, // Additional for larger sets
    { top: '160px', left: '150px' }
  ],
  Frameworks: [
    { top: '100px', left: '-100px' }, 
    { top: '130px', left: '0px' },    
    { top: '100px', left: '100px' }
  ],
  'Developer Tools': [
    { top: '100px', left: '-100px' }, 
    { top: '130px', left: '0px' },    
    { top: '100px', left: '100px' },  
    { top: '160px', left: '-150px' }, 
    { top: '160px', left: '150px' }
  ],
};

const branches = [
  { text: 'Languages', top: '10%', left: '10%', color: '#4CAF50' },
  { text: 'Frameworks', top: '10%', left: '70%', color: '#E91E63' },
  { text: 'Developer Tools', top: '50%', left: '40%', color: '#00BCD4' },
];

const subSkills = {
  Languages: ['Python', 'R', 'Java', 'Go', 'SQL', 'JavaScript'],
  Frameworks: ['ReactJS', 'Selenium', 'JUnit', 'TestNG'],
  'Developer Tools': ['Git', 'Docker', 'Kubernetes', 'Maven', 'Jenkins', 'Terraform', 'Azure', 'AWS'],
};

const TreeBranch = ({ text, color, top, left, onHover, onLeave }) => {
  return (
    <Branch
      style={{ top, left }}
      color={color}
      onMouseEnter={() => onHover(text)}
      onMouseLeave={onLeave}
      whileHover={{ scale: 1.1 }}
      transition={{ duration: 0.3 }}
    >
      {text}
    </Branch>
  );
};

export const SkillsTree = () => {
  const [hoveredSkill, setHoveredSkill] = useState(null);

  const handleHover = (skill) => {
    setHoveredSkill(skill);
  };

  const handleLeave = () => {
    setHoveredSkill(null);
  };

  return (
    <TreeContainer>
      <h2>Tree of Skills</h2>
      {branches.map((branch, index) => (
        <TreeBranch
          key={index}
          text={branch.text}
          color={branch.color}
          top={branch.top}
          left={branch.left}
          onHover={handleHover}
          onLeave={handleLeave}
        />
      ))}

      {/* Render sub-skills in an arc shape below the hovered skill */}
      {hoveredSkill &&
        subSkills[hoveredSkill].map((subSkill, index) => (
          <SubSkill
            key={index}
            style={{
              top: `calc(${branches.find(b => b.text === hoveredSkill).top} + ${subSkillArcPositions[hoveredSkill][index]?.top || '0px'})`,
              left: `calc(${branches.find(b => b.text === hoveredSkill).left} + ${subSkillArcPositions[hoveredSkill][index]?.left || '0px'})`,
            }}
            visible={hoveredSkill !== null}
            animate={{ opacity: hoveredSkill ? 1 : 0 }}
            transition={{ duration: 0.3 }}
          >
            {subSkill}
          </SubSkill>
        ))}
    </TreeContainer>
  );
};
