import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-scroll';
import Me from '../assets/Me.jpg';

const HomeSection = styled.section`
  display: flex;
  font-family: 'Pacifico', cursive;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4em 2em;
  background: linear-gradient(135deg, #333, #4ca1af);
  color: white;
  text-align: center;
  min-height: 100vh;
  position: relative;
  overflow: hidden;
`;

const HeroTitle = styled.h1`
  font-size: 4em;
  margin-bottom: 0.5em;
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 1.5em;
  white-space: nowrap;
  overflow: hidden;
  border-right: 2px solid rgba(255, 255, 255, 0.75);
  animation: typing 3.5s steps(40, end), blink 0.75s step-end infinite;
  
  @keyframes typing {
    from { width: 0 }
    to { width: 28% }
  }
  
  @keyframes blink {
    from, to { border-color: transparent }
    50% { border-color: white; }
  }
`;

const AnimatedPhoto = styled(motion.img)`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  border: 5px solid white;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.5);
`;

const Quote = styled(motion.div)`
  font-size: 1.2em;
  color: #f5f5dc;
  max-width: 600px;
  text-align: center;
  margin-top: 1.5em;
  font-style: italic;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1em;
  margin-top: 2em;
`;

// Enhanced NavButton styling for a fancier look
const NavButton = styled(motion.div)`
  display: inline-block;
  padding: 0.8em 1.5em;
  border-radius: 30px;
  background: linear-gradient(135deg, #84b067, #4ca1af);
  color: white;
  font-weight: bold;
  text-transform: uppercase;
  text-align: center;
  cursor: pointer;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.3);
  transition: transform 0.2s, background 0.3s ease;
  overflow: hidden;
  position: relative;

  &:hover {
    background: linear-gradient(135deg, #4ca1af, #84b067);
    transform: scale(1.3);
    z-index: 10;
  }

  &:before {
    content: '';
    position: absolute;
    top: 0;
    left: -75%;
    width: 50%;
    height: 100%;
    background: rgba(255, 255, 255, 0.2);
    transform: skewX(-25deg);
    transition: left 0.7s ease;
  }

  &:hover:before {
    left: 150%;
  }
`;

const Home = () => {
  return (
    <HomeSection>
      <AnimatedPhoto src={Me} alt="Tanishq Jain" />
      <HeroTitle>Hi, I'm Tanishq Jain</HeroTitle>
      <HeroSubtitle>DevOps Engineer | Full-Stack Developer</HeroSubtitle>
      <Quote initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 2, delay: 1 }}>
        "Turning ideas into reality through code and automation."
      </Quote>

      <ButtonContainer>
        <Link to="about" smooth={true} duration={800}>
          <NavButton>About</NavButton>
        </Link>
        <Link to="experience" smooth={true} duration={800}>
          <NavButton>Experience</NavButton>
        </Link>
        <Link to="skills" smooth={true} duration={800}>
          <NavButton>Skills</NavButton>
        </Link>
        <Link to="projects" smooth={true} duration={800}>
          <NavButton>Projects</NavButton>
        </Link>
        <Link to="contact" smooth={true} duration={800}>
          <NavButton>Contact</NavButton>
        </Link>
      </ButtonContainer>
    </HomeSection>
  );
};

export default Home;
