import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const TreeContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  position: relative;
  height: 600px;
  width: 800px;
  margin: auto;
`;

const Branch = styled(motion.div)`
  position: absolute;
  background-color: ${({ color }) => color || '#ddd'};
  border-radius: 50%;
  padding: 1em;
  width: 150px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  color: white;
  font-weight: bold;
  cursor: pointer;
  z-index: 1;
`;

const SubSkill = styled(motion.div)`
  position: absolute;
  top: ${({ top }) => top};
  left: ${({ left }) => left};
  background-color: #f9f9f9;
  padding: 0.5em;
  border: 1px solid #ddd;
  border-radius: 5px;
  z-index: 2;
  opacity: 0;
  pointer-events: none; /* Subskills should only appear on hover of a main skill */
  transition: opacity 0.3s ease-in-out;
`;

const branches = [
  { text: 'Cognitive', top: '10%', left: '10%', color: '#4CAF50' },
  { text: 'Leadership', top: '10%', left: '70%', color: '#E91E63' },
  { text: 'Collaboration', top: '50%', left: '80%', color: '#00BCD4' },
  { text: 'Creativity', top: '50%', left: '10%', color: '#FFC107' },
  { text: 'Emotional', top: '75%', left: '30%', color: '#FF9800' },
  { text: 'Social & Civic Engagement', top: '75%', left: '60%', color: '#3F51B5' },
];

const subSkills = {
  Cognitive: ['Problem Solving', 'Critical Thinking', 'System Thinking'],
  Leadership: ['Entrepreneurship', 'Organizing', 'Influence'],
  Collaboration: ['Negotiation', 'Conflict Mgmt.', 'Communication'],
  Creativity: ['Curiosity', 'Innovation', 'Visualization'],
  Emotional: ['Empathy', 'Resilience', 'Self-awareness'],
  'Social & Civic Engagement': ['Citizenship', 'Sustainability', 'Equality'],
};

const TreeBranch = ({ text, color, top, left, onHover, onLeave }) => {
  return (
    <Branch
      style={{ top, left }}
      color={color}
      onMouseEnter={() => onHover(text)}
      onMouseLeave={() => onLeave()}
      whileHover={{ scale: 1.1 }}
      transition={{ duration: 0.3 }}
    >
      {text}
    </Branch>
  );
};

export const SkillsTree = () => {
  const [hoveredSkill, setHoveredSkill] = useState(null);

  const handleHover = (skill) => {
    setHoveredSkill(skill);
  };

  const handleLeave = () => {
    setHoveredSkill(null);
  };

  return (
    <TreeContainer>
      <h2>Tree of Skills</h2>
      {branches.map((branch, index) => (
        <TreeBranch
          key={index}
          text={branch.text}
          color={branch.color}
          top={branch.top}
          left={branch.left}
          onHover={handleHover}
          onLeave={handleLeave}
        />
      ))}

      {hoveredSkill && subSkills[hoveredSkill].map((subSkill, index) => (
        <SubSkill
          key={index}
          style={{ top: `${Math.random() * 50 + 30}%`, left: `${Math.random() * 50 + 20}%` }} // Random placement for subskills
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
        >
          {subSkill}
        </SubSkill>
      ))}
    </TreeContainer>
  );
};
