import React from 'react';
import styled from 'styled-components';

// Styled container for the experience section
const ExperienceSection = styled.section`
  background-color: #f7f7f7;
  padding: 4em 2em;
  text-align: center;
`;

// Title for the section
const Title = styled.h1`
  font-size: 2.5em;
  color: #333;
  margin-bottom: 1em;
`;

// Experience list container
const ExperienceList = styled.div`
  max-width: 800px;
  margin: auto;
  text-align: left;
`;

// Experience item
const ExperienceItem = styled.div`
  margin-bottom: 2em;
`;

const JobTitle = styled.h3`
  font-size: 1.5em;
  color: #4CAF50;
  margin-bottom: 0.5em;
`;

const Company = styled.h4`
  font-size: 1.2em;
  color: #333;
`;

const Timeline = styled.h5`
  font-size: 1em;
  color: #666;
`;

const Description = styled.p`
  font-size: 1em;
  color: #666;
`;

const Experience = () => {
  return (
    <ExperienceSection>
      <Title>My Experience</Title>
      <ExperienceList>
        
        {/* BOSCH Experience */}
        <ExperienceItem>
          <JobTitle>Associate Software Engineer (DevOps & Full Stack)</JobTitle>
          <Company>BOSCH Global Software Technologies, Bangalore, India</Company>
          <Timeline>Jul. 2023 - Present</Timeline>
          <Description>
            <strong>DevOps Engineer Role:</strong><br />
            ∗ Architected and maintained CI/CD pipelines, automating testing, building, and deployment processes, slashing deployment time by 60% and improving application availability by 20%.<br />
            ∗ Engineered a custom Jenkins Plugin that streamlined internal processes, reducing build times by 40% and increasing deployment accuracy by 25%.<br />
            ∗ Led Docker containerization, boosting application scalability by 30% and reducing deployment time by 50%.<br />
            ∗ Implemented auto-scaling in Kubernetes, cutting manual intervention by 60% and saving 20 hours per week.<br />
            ∗ Authored Python and Shell automation scripts, saving 20 hours weekly and improving task completion by 35%.<br /><br />
            
            <strong>Full Stack Engineer Role:</strong><br />
            ∗ Developed “docs-as-code” framework reducing manual effort by 40 hours monthly.<br />
            ∗ Created an automated visualization tool integrating Sphinx with Graphviz and PlantUML, saving 15 hours monthly on visual tasks.<br />
            ∗ Integrated CI/CD-driven documentation solution with SCM tools like GitHub and Azure DevOps, ensuring sync with software releases.<br />
            ∗ Deployed RBAC strategy on cloud infrastructure, ensuring secure project document visibility with zero data breaches.<br />
            ∗ Automated Sphinx documentation pipeline, reducing manual formatting time by 15 hours weekly.
          </Description>
        </ExperienceItem>

        {/* Google Summer of Code Experience */}
        <ExperienceItem>
          <JobTitle>Google Summer of Code Contributor</JobTitle>
          <Company>Google Summer of Code 2022 - PEcAn Projects</Company>
          <Timeline>Jun. 2022 - Sept 2022</Timeline>
          <Description>
            ∗ Refactored 10+ R packages for CRAN compliance, improving code quality and maintainability by 30%.<br />
            ∗ Engineered critical R functions to eliminate non-CRAN dependencies, reducing software maintenance by 40%.<br />
            ∗ Optimized download process for PEcAn packages, improving download speeds by 40% via streamlined installation.<br />
          </Description>
        </ExperienceItem>

      </ExperienceList>
    </ExperienceSection>
  );
};

export default Experience;
