import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Styled components for the section and project cards
const ProjectsSection = styled(motion.section)`
  padding: 4em;
  background: #f4f4f4;
  color: #333;
  text-align: center;
`;

const ProjectCard = styled(motion.div)`
  padding: 1em;
  border: 1px solid #ddd;
  margin: 1em 0;
  background: white;
  transition: transform 0.3s ease;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.1);
  border-radius: 8px;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.2);
  }
`;

export const Projects = () => {
  return (
    <ProjectsSection
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 1.5 }}
    >
      <h2>Projects</h2>

      <ProjectCard
        whileHover={{ scale: 1.1 }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.2 }}
      >
        <h3>Virtual Dressing Room</h3>
        <p>Developed a virtual dressing room application using Python, AIML, and OpenCV. Users can upload and try on garments in a 3D environment.</p>
        <a href="https://github.com/nanu1605/Virtual-Dressing-Room" target="_blank" rel="noopener noreferrer">View on GitHub</a>
      </ProjectCard>

      <ProjectCard
        whileHover={{ scale: 1.1 }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <h3>Attendance System</h3>
        <p>Built an attendance system using Java and XML for managing students, faculty, and teachers efficiently.</p>
        <a href="https://github.com/nanu1605/Attendence-System" target="_blank" rel="noopener noreferrer">View on GitHub</a>
      </ProjectCard>

      {/* Add more project cards as needed */}
    </ProjectsSection>
  );
};
