import React from 'react';
import { Tree } from 'react-tree-graph';  // Import Tree as a named export
import 'react-tree-graph/dist/style.css';
import styled from 'styled-components';

const TreeSection = styled.section`
  padding: 4em;
  background: #fff;
  color: #333;
  text-align: center;

  .tree {
    width: 100%;
    height: 500px;
  }
`;

const data = {
  name: 'Skills',
  children: [
    {
      name: 'Languages',
      children: [
        { name: 'Python' },
        { name: 'R' },
        { name: 'Java' },
        { name: 'Go' },
        { name: 'SQL' },
        { name: 'JavaScript' },
      ],
    },
    {
      name: 'Frameworks & Libraries',
      children: [
        { name: 'ReactJS' },
        { name: 'Selenium' },
        { name: 'JUnit' },
        { name: 'TestNG' },
      ],
    },
    {
      name: 'DevOps & Cloud Tools',
      children: [
        { name: 'Docker' },
        { name: 'Kubernetes' },
        { name: 'Jenkins' },
        { name: 'Terraform' },
        { name: 'Maven' },
        { name: 'Azure' },
        { name: 'AWS' },
      ],
    },
  ],
};

export const SkillsTree = () => {
  return (
    <TreeSection>
      <h2>Skills</h2>
      <Tree
        data={data}
        height={400}
        width={800}
        animated
        svgProps={{
          transform: 'rotate(90)',
          style: { margin: 'auto' },
        }}
      />
    </TreeSection>
  );
};
