import React from 'react';
import styled from 'styled-components';
import { SkillsCard } from './SkillsCard';

// Styled components for the grid layout
const GridContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 2em;
  justify-items: center;
  padding: 2em;
`;

// Sample data for skills categories
const skillsData = [
  {
    title: 'Languages',
    skills: ['Python', 'R', 'Java', 'Go', 'SQL', 'JavaScript'],
    color: '#4CAF50',
  },
  {
    title: 'Frameworks',
    skills: ['ReactJS', 'Selenium', 'JUnit', 'TestNG'],
    color: '#E91E63',
  },
  {
    title: 'Developer Tools',
    skills: ['Git', 'Docker', 'Kubernetes', 'Maven', 'Jenkins', 'Terraform', 'Azure', 'AWS'],
    color: '#00BCD4',
  },
];

export const SkillsGrid = () => {
  return (
    <GridContainer>
      {skillsData.map((category, index) => (
        <SkillsCard
          key={index}
          title={category.title}
          skills={category.skills}
          color={category.color}
        />
      ))}
    </GridContainer>
  );
};
