import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import Me from '../assets/Me.jpg';

// Section container
const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4em 2em;
  background: linear-gradient(135deg, #333, #4ca1af);
  color: white;
  text-align: center;
  min-height: 100vh;
  overflow: hidden;
  position: relative;
`;

// Rotating background circle
const rotate = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const RotatingCircle = styled.div`
  position: absolute;
  width: 600px;
  height: 600px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 50%;
  animation: ${rotate} 20s linear infinite;
  top: -150px;
  left: -150px;
`;

// Title and subtitle styling
const HeroTitle = styled.h1`
  font-size: 3.5em;
  margin-bottom: 0.5em;
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 1.5em;
  color: #f5f5dc;
  animation: fade 3s ease-in-out infinite alternate;
  @keyframes fade {
    from { opacity: 1; }
    to { opacity: 0.5; }
  }
`;

// Animated photo
const AnimatedPhoto = styled(motion.img)`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  border: 5px solid white;
  margin-top: 1em;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.5);
`;


// Quote container
const Quote = styled(motion.div)`
  font-size: 1.2em;
  color: #f5f5dc;
  max-width: 600px;
  text-align: center;
  margin-top: 1.5em;
  font-style: italic;
`;

const ContactButton = styled(motion.a)`
  background-color: #84b067;
  color: white;
  padding: 0.8em 1.5em;
  border-radius: 25px;
  text-transform: uppercase;
  font-weight: bold;
  margin-top: 2em;
  text-decoration: none;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.3);
  
  &:hover {
    background-color: #4ca1af;
  }
`;

const Home = () => {
  return (
    <HomeSection>
      <RotatingCircle />

      <HeroTitle>Hi, I'm Tanishq Jain</HeroTitle>
      <HeroSubtitle>DevOps Engineer | Full-Stack Developer</HeroSubtitle>

      <AnimatedPhoto 
        src={Me} 
        alt="Tanishq Jain" 
        animate={{ y: [0, -10, 0] }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      />

      {/* Quote Section */}
      <Quote initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 2, delay: 1 }}>
        "Turning ideas into reality through code and automation."
      </Quote>

      {/* Contact Button */}
      <ContactButton href="#contact">
        Contact Me
      </ContactButton>
    </HomeSection>
  );
};

export default Home;
