import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Section container for About Me
const AboutSection = styled.section`
  padding: 4em;
  background: linear-gradient(135deg, #84b067 0%, #333 100%);
  color: white;
  text-align: center;
  min-height: 100vh;
`;

// Updated title with a controlled color (you can change the color as needed)
const Title = styled(motion.h2)`
  font-size: 3em;
  margin-bottom: 1em;
  color: white; /* Explicitly setting the color to white or another color of your choice */
  text-transform: uppercase;
  letter-spacing: 3px;
`;

const About = () => {
  return (
    <AboutSection>
      <Title
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        About Me
      </Title>

      <p>
        I'm a DevOps Engineer at BOSCH Global Software Technologies with experience in automating CI/CD pipelines, Docker, Kubernetes, and more. I am passionate about optimizing deployment processes and ensuring smooth and scalable infrastructures.
      </p>
    </AboutSection>
  );
};

export default About;
