import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Me from '../assets/Me.jpg';

const HomeSection = styled.section`
  display: flex;
  font-family: 'Pacifico', cursive;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4em 2em;
  background: linear-gradient(135deg, #333, #4ca1af);
  color: white;
  text-align: center;
  min-height: 100vh;
  position: relative;
  overflow: hidden;
`;

const HeroTitle = styled.h1`
  font-size: 4em;
  margin-bottom: 0.5em;
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 1.5em;
  white-space: nowrap;
  overflow: hidden;
  border-right: 2px solid rgba(255, 255, 255, 0.75);
  animation: typing 3.5s steps(40, end), blink 0.75s step-end infinite;
  
  @keyframes typing {
    from { width: 0 }
    to { width: 28% }
  }
  
  @keyframes blink {
    from, to { border-color: transparent }
    50% { border-color: white; }
  }
`;

const AnimatedPhoto = styled(motion.img)`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  border: 5px solid white;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.5);
`;

const Quote = styled(motion.div)`
  font-size: 1.2em;
  color: #f5f5dc;
  max-width: 600px;
  text-align: center;
  margin-top: 1.5em;
  font-style: italic;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1em;
  margin-top: 2em;
`;

const NavButton = styled(motion.button)`
  background-color: #84b067;
  color: white;
  padding: 0.8em 1.5em;
  border-radius: 25px;
  text-transform: uppercase;
  font-weight: bold;
  border: none;
  cursor: pointer;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.3);
  transition: background-color 0.3s ease;
  
  &:hover {
    background-color: #4ca1af;
  }
`;

const scrollToSection = (sectionId) => {
  const section = document.getElementById(sectionId);
  if (section) {
    section.scrollIntoView({ behavior: 'smooth' });
  }
};

const Home = () => {
  return (
    <HomeSection>
      <AnimatedPhoto src={Me} alt="Tanishq Jain" />
      <HeroTitle>Hi, I'm Tanishq Jain</HeroTitle>
      <HeroSubtitle>DevOps Engineer | Full-Stack Developer</HeroSubtitle>
      <Quote initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 2, delay: 1 }}>
        "Turning ideas into reality through code and automation."
      </Quote>

      <ButtonContainer>
        <NavButton onClick={() => scrollToSection("about")}>About</NavButton>
        <NavButton onClick={() => scrollToSection("skills")}>Skills</NavButton>
        <NavButton onClick={() => scrollToSection("projects")}>Projects</NavButton>
        <NavButton onClick={() => scrollToSection("contact")}>Contact</NavButton>
      </ButtonContainer>
    </HomeSection>
  );
};

export default Home;
