import React from 'react';
import { SkillsGrid } from './components/SkillsGrid'; // Import the SkillsGrid component
import styled from 'styled-components';

// Styled containers for the different sections of the portfolio
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  font-family: 'Arial', sans-serif;
  background-color: #f0f0f0;
`;

const Section = styled.section`
  width: 100%;
  padding: 4em 2em;
  text-align: center;
  background-color: ${({ bgColor }) => bgColor || '#fff'};
`;

const Title = styled.h1`
  font-size: 2.5em;
  color: #333;
  margin-bottom: 1em;
`;

// Hero Section - Your Introduction
const HeroSection = styled(Section)`
  background-color: #333;
  color: white;
`;

const HeroTitle = styled.h1`
  font-size: 4em;
  margin-bottom: 0.5em;
`;

const HeroSubtitle = styled.p`
  font-size: 1.5em;
`;

// About Me Section
const AboutMeSection = styled(Section)`
  background-color: #f7f7f7;
`;

const AboutText = styled.p`
  font-size: 1.2em;
  max-width: 800px;
  margin: auto;
`;

// Projects Section
const ProjectsSection = styled(Section)`
  background-color: #fff;
`;

const ProjectGrid = styled.div`
  display: flex;
  justify-content: center;
  gap: 2em;
  flex-wrap: wrap;
`;

const ProjectCard = styled.div`
  background-color: #fff;
  padding: 2em;
  border-radius: 10px;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.1);
  width: 250px;
  text-align: center;
`;

// Contact Section
const ContactSection = styled(Section)`
  background-color: #eaeaea;
`;

const ContactButton = styled.a`
  background-color: #4CAF50;
  color: white;
  padding: 1em 2em;
  text-decoration: none;
  border-radius: 5px;
  margin-top: 1em;
  display: inline-block;
`;

function App() {
  return (
    <AppContainer>

      {/* Hero Section */}
      <HeroSection>
        <HeroTitle>Hi, I'm Tanishq Jain</HeroTitle>
        <HeroSubtitle>DevOps Engineer | Full-Stack Developer</HeroSubtitle>
      </HeroSection>

      {/* About Me Section */}
      <AboutMeSection>
        <Title>About Me</Title>
        <AboutText>
          I'm a passionate DevOps Engineer with experience in Docker, Kubernetes, and building CI/CD pipelines.
          I've worked on various projects ranging from cloud infrastructure to full-stack development.
          I love to build and optimize software systems that drive innovation and improve efficiency.
        </AboutText>
      </AboutMeSection>

      {/* Projects Section */}
      <ProjectsSection>
        <Title>My Projects</Title>
        <ProjectGrid>
          <ProjectCard>
            <h3>Virtual Dressing Room</h3>
            <p>Developed a virtual dressing room using Python, OpenCV, and machine learning algorithms.</p>
          </ProjectCard>
          <ProjectCard>
            <h3>Attendance System</h3>
            <p>A Java-based attendance management system for schools and institutions.</p>
          </ProjectCard>
          {/* Add more ProjectCards as needed */}
        </ProjectGrid>
      </ProjectsSection>

      {/* Skills Section */}
      <Section>
        <Title>My Skills</Title>
        <SkillsGrid /> {/* SkillsGrid component from earlier */}
      </Section>

      {/* Contact Section */}
      <ContactSection>
        <Title>Contact Me</Title>
        <p>If you'd like to get in touch, feel free to reach out via email or LinkedIn.</p>
        <ContactButton href="mailto:example@example.com">Send me an Email</ContactButton>
      </ContactSection>

    </AppContainer>
  );
}

export default App;
