import React from 'react';
import styled from 'styled-components';

// Styled container for the contact section
const ContactSection = styled.section`
  padding: 4em 2em;
  text-align: center;
  background-color: #f7f7f7;
`;

const ContactTitle = styled.h2`
  font-size: 2.5em;
  margin-bottom: 1em;
`;

const ContactInfo = styled.div`
  margin-bottom: 2em;
  font-size: 1.2em;
  color: #333;
`;

const ContactButton = styled.a`
  background-color: #4CAF50;
  color: white;
  padding: 1em 2em;
  text-decoration: none;
  border-radius: 5px;
  margin-top: 1em;
  display: inline-block;
  font-size: 1.2em;

  &:hover {
    background-color: #45a049;
  }
`;

const ContactLink = styled.a`
  color: #0077b5;
  text-decoration: none;
  font-size: 1.2em;
  margin-top: 0.5em;
  display: block;

  &:hover {
    text-decoration: underline;
  }
`;

const Contact = () => {
  return (
    <ContactSection>
      <ContactTitle>Contact Me</ContactTitle>

      {/* Email button */}
      <ContactButton href="mailto:your-email@example.com">
        Send me an Email
      </ContactButton>

      {/* Contact Information */}
      <ContactInfo>
        <p>Mobile: +1234567890</p> {/* Replace with your actual mobile number */}
        <p>Slack: @your-slack-username</p> {/* Replace with your actual Slack username */}
      </ContactInfo>

      {/* Links */}
      <ContactLink href="https://www.linkedin.com/in/your-linkedin-profile" target="_blank">
        LinkedIn Profile
      </ContactLink>
      <ContactLink href="https://github.com/your-github-profile" target="_blank">
        GitHub Profile
      </ContactLink>
    </ContactSection>
  );
};

export default Contact;
