import styled from 'styled-components';
import { motion } from 'framer-motion';

export const ParticleContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: -1;
  overflow: hidden;
  background: radial-gradient(circle at 50% 50%, rgba(255, 255, 255, 0.1), transparent 50%);
`;

export const ExperienceSection = styled.section`
  position: relative;
  background: linear-gradient(135deg, #F5F5DC 0%, #4ca1af 100%);
  padding: 4em 2em;
  text-align: center;
  overflow: hidden;
`;

export const Title = styled.h1`
  font-size: 3em;
  font-family: 'Pacifico', cursive;
  color: #333;
  margin-bottom: 2em;
  text-transform: uppercase;
  letter-spacing: 3px;
`;

export const ExperienceList = styled(motion.div)`
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 3em;
  perspective: 1000px;
`;



export const JobTitle = styled.h3`
  font-size: 2.2em;
  color: #fff;
  font-weight: bold;
  margin-bottom: 0.5em;
`;

export const Company = styled.h4`
  font-size: 1.5em;
  color: #fff;
  font-weight: bold;
  margin-bottom: 1em;
`;

export const Timeline = styled.h5`
  font-size: 1.1em;
  color: #ccc;
  margin-bottom: 1em;
`;

export const Description = styled.p`
  font-size: 1.2em;
  color: #fff;
  line-height: 1.8;
`;
